// src/api/link.js
import httpFactory from "./httpFactory";

// Set your link service URL here (or via env var)
// If you run the link FastAPI on a different port/host, set REACT_APP_LINK_API
const http = httpFactory(process.env.REACT_APP_LINK_API || "http://34.248.112.237:8004");

export const createLink = (payload) => http.post("/link", payload);       // {mobile_id, video_name, shop_name, gname}
export const listLinks  = (params = {}) => http.get("/links", { params }); // {limit, offset, mobile_id, ...}
export const deleteLink = (id) => http.delete(`/link/${id}`);
