import React, { useEffect, useState } from "react";
import { insertShop, listShops, updateShop, deleteShop } from "../api/shop";

export default function Shop() {
  const [items, setItems] = useState([]);
  const [q, setQ] = useState("");
  const [name, setName] = useState("");
  const [renameTo, setRenameTo] = useState("");

  const load = async () => {
    const res = await listShops({ q, limit: 50, offset: 0 });
    setItems(res.data.items);
  };

  useEffect(() => { load(); }, []);

  const add = async () => {
    if (!name.trim()) return;
    await insertShop(name.trim());
    setName("");
    load();
  };

  const rename = async (shop_name) => {
    if (!renameTo.trim()) return;
    await updateShop(shop_name, { shop_name: renameTo.trim() });
    setRenameTo("");
    load();
  };

  const remove = async (shop_name) => {
    await deleteShop(shop_name);
    load();
  };

  return (
    <div>
      <h2>Shops</h2>

      <div style={{ display: "flex", gap: 8, marginBottom: 10 }}>
        <input placeholder="search (q)" value={q} onChange={(e) => setQ(e.target.value)} />
        <button onClick={load}>Search</button>
      </div>

      <div style={{ display: "flex", gap: 8, marginBottom: 10 }}>
        <input placeholder="new shop name" value={name} onChange={(e) => setName(e.target.value)} />
        <button onClick={add}>Add Shop</button>
      </div>

      <ul>
        {items.map((it) => (
          <li key={it.id}>
            <strong>{it.shop_name}</strong> &nbsp; (id: {it.id})
            <div style={{ display: "inline-flex", gap: 8, marginLeft: 12 }}>
              <input placeholder="rename to..." value={renameTo} onChange={(e) => setRenameTo(e.target.value)} />
              <button onClick={() => rename(it.shop_name)}>Rename</button>
              <button onClick={() => remove(it.shop_name)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
