// src/components/Linker.js
import React, { useEffect, useState } from "react";
import { listDevices } from "../api/device";
import { listGroups } from "../api/group";
import { listShops } from "../api/shop";
import { listVideos } from "../api/video";
import { createLink } from "../api/link";

// MUI
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import CircularProgress from "@mui/material/CircularProgress";

export default function Linker() {
  // lists
  const [devices, setDevices] = useState([]);
  const [groups, setGroups] = useState([]);
  const [shops, setShops] = useState([]);
  const [videos, setVideos] = useState([]);

  // selections – store the final strings we will POST
  const [mobileId, setMobileId] = useState("");
  const [gname, setGname] = useState("");
  const [shopName, setShopName] = useState("");
  const [videoName, setVideoName] = useState("");

  // ui
  const [busy, setBusy] = useState(false);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    loadAll();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Retry helper for endpoints that 422 on high limits
  const withLimitFallback = async (fn, params, caps = [100, 50]) => {
    try {
      const r = await fn(params);
      return r.data.items || [];
    } catch (e) {
      if (e?.response?.status === 422 && params?.limit) {
        for (const cap of caps) {
          try {
            const r2 = await fn({ ...params, limit: cap });
            return r2.data.items || [];
          } catch (e2) {
            if (e2?.response?.status !== 422) throw e2;
          }
        }
        const r3 = await fn({});
        return r3.data.items || [];
      }
      throw e;
    }
  };

  const loadAll = async () => {
    setBusy(true);
    setMsg("");
    try {
      const MAX = 100;
      const [d, g, s, v] = await Promise.all([
        listDevices().then((r) => r.data.items || []),
        withLimitFallback(listGroups, { limit: MAX, offset: 0 }),
        withLimitFallback(listShops, { limit: MAX, offset: 0 }),
        withLimitFallback(listVideos, { limit: MAX, offset: 0 }),
      ]);
      setDevices(d);
      setGroups(g);
      setShops(s);
      setVideos(v);
    } catch (e) {
      setMsg(`Failed to load: ${e?.response?.data?.detail || e.message}`);
    } finally {
      setBusy(false);
    }
  };

  const clear = () => {
    setMobileId("");
    setGname("");
    setShopName("");
    setVideoName("");
  };

  const add = async () => {
    if (!mobileId || !gname || !shopName || !videoName) {
      setMsg("Please select Device, Group, Shop, and Video.");
      return;
    }
    setBusy(true);
    setMsg("");
    try {
      await createLink({ mobile_id: mobileId, gname, shop_name: shopName, video_name: videoName });
      setMsg("Link created ✅");
      clear();
    } catch (e) {
      setMsg(`Failed: ${e?.response?.data?.detail || e.message}`);
    } finally {
      setBusy(false);
    }
  };

  // styles
  const btn = { padding: "8px 12px", borderRadius: 8, border: "1px solid #e5e7eb", background: "#fff", cursor: "pointer", fontSize: 14, fontWeight: 600 };
  const btnPrimary = { ...btn, background: "#4f46e5", color: "#fff", borderColor: "#4f46e5" };
  const grid2 = { display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 };

  // Autocomplete helpers that accept either option objects or plain strings
  const pick = (val, key) => (typeof val === "string" ? val : val?.[key] || "");
  const loadingAdornment = busy ? { endAdornment: (<><CircularProgress color="inherit" size={16} /> </>) } : {};

  return (
    <div style={{ background: "#fff" }}>
      <h2 style={{ fontSize: 18, margin: "0 0 12px" }}>
        Create Relation (Device • Group • Shop • Video)
      </h2>

      <div style={grid2}>
        {/* Device (mobile_id) */}
        <Autocomplete
          disablePortal
          freeSolo
          size="small"
          options={devices}
          getOptionLabel={(opt) =>
            typeof opt === "string" ? opt : opt?.mobile_id || ""
          }
          value={mobileId}
          inputValue={mobileId}
          onInputChange={(_, newInput) => setMobileId(newInput)}
          onChange={(_, newValue) => setMobileId(pick(newValue, "mobile_id"))}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Device (mobile_id)"
              InputProps={{ ...params.InputProps, ...loadingAdornment }}
            />
          )}
        />

        {/* Group (gname) */}
        <Autocomplete
          disablePortal
          freeSolo
          size="small"
          options={groups}
          getOptionLabel={(opt) =>
            typeof opt === "string" ? opt : opt?.gname || ""
          }
          value={gname}
          inputValue={gname}
          onInputChange={(_, newInput) => setGname(newInput)}
          onChange={(_, newValue) => setGname(pick(newValue, "gname"))}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Group (gname)"
              InputProps={{ ...params.InputProps, ...loadingAdornment }}
            />
          )}
        />

        {/* Shop (shop_name) */}
        <Autocomplete
          disablePortal
          freeSolo
          size="small"
          options={shops}
          getOptionLabel={(opt) =>
            typeof opt === "string" ? opt : opt?.shop_name || ""
          }
          value={shopName}
          inputValue={shopName}
          onInputChange={(_, newInput) => setShopName(newInput)}
          onChange={(_, newValue) => setShopName(pick(newValue, "shop_name"))}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Shop (shop_name)"
              InputProps={{ ...params.InputProps, ...loadingAdornment }}
            />
          )}
        />

        {/* Video (video_name) */}
        <Autocomplete
          disablePortal
          freeSolo
          size="small"
          options={videos}
          getOptionLabel={(opt) =>
            typeof opt === "string" ? opt : opt?.video_name || ""
          }
          value={videoName}
          inputValue={videoName}
          onInputChange={(_, newInput) => setVideoName(newInput)}
          onChange={(_, newValue) => setVideoName(pick(newValue, "video_name"))}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Video (video_name)"
              InputProps={{ ...params.InputProps, ...loadingAdornment }}
            />
          )}
        />
      </div>

      <div style={{ display: "flex", gap: 8, marginTop: 14, alignItems: "center" }}>
        <button style={btn} onClick={loadAll} disabled={busy}>Reload lists</button>
        <button style={btnPrimary} onClick={add} disabled={busy}>Create Link</button>
        {msg && (
          <span
            style={{
              marginLeft: 8,
              color: msg.startsWith("Failed") ? "#dc2626" : "#16a34a",
              fontSize: 13,
            }}
          >
            {busy ? "Working…" : msg}
          </span>
        )}
      </div>
    </div>
  );
}
